C++<pre><code>std::wstring  CGlobalHelper::read_utf8_file(const std::string &amp;path)
{
    std::locale::global(std::locale("en_US.UTF-8"));

    // 打开文件
    std::ifstream file(path.c_str());

    if (!file.is_open()) {
        return L"";
    }

    // 读取文件内容并存储到wstring中
    /*std::wstring_convert&lt;std::codecvt_utf8&lt;wchar_t&gt;&gt; converter;
    std::wstring content((std::istreambuf_iterator&lt;char&gt;(file)),
        std::istreambuf_iterator&lt;char&gt;());
    file.close();*/
    std::wstring content;
    file.seekg(0, std::ios::end);
    content.reserve(file.tellg());
    file.seekg(0, std::ios::beg);

    content.assign((std::istreambuf_iterator&lt;char&gt;(file)),
        std::istreambuf_iterator&lt;char&gt;());

    // 关闭文件
    file.close();
    return content;
}
</code></pre>

C++<pre><code>std::wstring CGlobalHelper::utf8ToUnicode(const char *pszU8)
{
    int    n = MultiByteToWideChar(CP_UTF8, 0, pszU8, -1, NULL, 0);
    WCHAR *pwsz = new WCHAR[n + 1];
    memset((void *)pwsz, 0, (n + 1) * sizeof(WCHAR));
    MultiByteToWideChar(CP_UTF8, 0, pszU8, -1, pwsz, n);

    std::wstring wstr = pwsz;
    delete[] pwsz;

    return wstr;
}
