    AcDbVoidPtrArray explodedEnts;
    es = pBlkRef-&gt;explode(explodedEnts);
    AcDbObjectId wmfBlockId = pBlkRef-&gt;blockTableRecord();

    if (es == Acad::eOk &amp;&amp; explodedEnts.length() &gt; 0)
