果CUI文件被修改，则保存             //保存CMDECHO及FILEDIA系统变量             object oldCmdEcho = Application.GetSystemVariable("CMDECHO");             object oldFileDia = Application.GetSystemVariable("FILEDIA");             //设置CMDECHO=0，控制不在命令行上回显提示和输入信息             Application.SetSystemVariable("CMDECHO", 0);             //设置FILEDIA=0，禁止显示文件对话框，这样可以通过程序输入文件名             Application.SetSystemVariable("FILEDIA", 0);             //获取当前活动文档             Document doc=Application.DocumentManager.MdiActiveDocument;             //获取主CUI文件             CustomizationSection mainCs=doc.GetMainCustomizationSection();              //如果已存在局部CUI文件，则先卸载             if (mainCs.PartialCuiFiles.Contains(cs.CUIFileName))                 doc.SendStringToExecute(".cuiunload " + cs.CUIFileBaseName + " ", false, false, false);             //装载CUI文件，注意文件名必须是带路径的             doc.SendStringToExecute(".cuiload " + cs.CUIFileName + " ", false, false, false);             //恢复CMDECHO及FILEDIA系统变量的初始值             doc.SendStringToExecute("(setvar \"FILEDIA\" " + oldFileDia.ToString() + ")(princ) ", false, false, false);             doc.SendStringToExecute("(setvar \"CMDECHO\" " + oldCmdEcho.ToString() + ")(princ) ", false, false, false);         }          ///          /// 添加菜单项所要执行的宏         ///          /// CUI文件         /// 宏的显示名称         /// 宏的具体命令         /// 宏的标识符         /// 宏的状态栏提示信息         /// 宏的图标         /// 返回创建的宏         public static MenuMacro AddMacro(this CustomizationSection source, string name, string command, string tag, string helpString, string imagePath)         {             MenuGroup menuGroup=source.MenuGroup;//获取CUI文件中的菜单组             //判断菜单组中是否已经定义与菜单组名相同的宏集合             MacroGroup mg=menuGroup.FindMacroGroup(menuGroup.Name);             if (mg == null)//如果宏集合没有定义，则创建一个与菜单组名相同的宏集合                 mg = new MacroGroup(menuGroup.Name, menuGroup);             //如果已经宏已经被定义，则返回             foreach (MenuMacro macro in mg.MenuMacros)             {                 if (macro.ElementID == tag)                     return null;             }              //在宏集合中创建一个命令宏             MenuMacro MenuMacro=new MenuMacro(mg, name, command, tag);             //指定命令宏的说明信息，在状态栏中显示             MenuMacro.macro.HelpString = helpString;             //指定命令宏的大小图像的路径             MenuMacro.macro.LargeImage = MenuMacro.macro.SmallImage = imagePath;             return MenuMacro;//返回命令宏         }          ///          /// 添加下拉菜单         ///          /// 包含菜单的菜单组         /// 菜单名         /// 菜单的别名         /// 菜单的标识字符串         /// 返回下拉菜单对象         public static PopMenu AddPopMenu(this MenuGroup menuGroup, string name, StringCollection aliasList, string tag)         {             PopMenu pm=null;//声明下拉菜单对象             //如果菜单组中没有名称为name的下拉菜单             if (menuGroup.PopMenus.IsNameFree(name))             {                 //为下拉菜单指定显示名称、别名、标识符和所属的菜单组                 pm = new PopMenu(name, aliasList, tag, menuGroup);             }             return pm;//返回下拉菜单对象         }          ///          /// 为菜单添加菜单项         ///          /// 菜单项所属的菜单         /// 菜单项的位置         /// 菜单项的显示名称         /// 菜单项的命令宏的Id         /// 返回添加的菜单项         public static PopMenuItem AddMenuItem(this PopMenu parentMenu, int index, string name, string macroId)         {             PopMenuItem newPmi=null;             //如果存在名为name的菜单项，则返回             foreach (PopMenuItem pmi in parentMenu.PopMenuItems)                 if (pmi.Name == name) return newPmi;             //定义一个菜单项对象，指定所属的菜单及位置             newPmi = new PopMenuItem(parentMenu, index);             ////如果name不为空，则指定菜单项的显示名为name，否则会使用命令宏的名称             if (name != null) newPmi.Name = name;             newPmi.MacroID = macroId;//菜单项的命令宏的ID<p></p>

